import React, {useState} from 'react';
import Header from "./Header";
import Footer from "./Footer";
import HomePage from "./HomePage";
import Posts from "./posts";

function App() {
  const [newContent, setNewContent] = useState("");
  const [posts, setPosts] = useState([]);
  const [page, setPage] = useState(true)


  function handlePageChanege(){
    if(page === false){
      setPage(true)
    }else {
      setPage(false)
    };
  }


  function handleChange (event){
    const newValue = event.target.value;
    return(
      setNewContent(newValue)
    )
  };


  function handleNewPost(event){
      const newPostContent = newContent;
      return(
        setPosts(prevPosts => [...prevPosts, newPostContent])

      )
  }

  return (
    <div >
    <div><Header onClick = {handlePageChanege}/></div>
    <div>
      {/* {(if (page === true){
        return (<HomePage/>)
      }else{
        return (<Posts/>)
      }
      )} */}
      {(() => {
        switch (page) {
          case true:   return <HomePage/>;
          case false: return <Posts/>;
        }
      })()}
    </div>
    <div><Footer/></div>
    </div>
  );
}

export default App;
