import Header from "./Header";
import Footer from "./Footer";
import Content from "./Content";

function App() {
  const [posts, setPosts] = useState([]);
  function handleNewPost(event){
      const newPostContent = event.target.value;
      setPosts([newPostContent])
  }
  return (
    <div >
    <div><Header/></div>
    <div><Content/></div>
    <div><Footer/></div>
    </div>
  );
}

export default App;
