  const [newContent, setNewContent] = useState("");
  const [posts, setPosts] = useState([]);


  function handleNewPost(event){
      const newPostContent = event.target.value;
      return(
        setPosts(prevPosts => [...prevPosts, newPostContent])

      )
  }


  return (
    <div >
    <div><Header/></div>
    <div><Content/></div>
    <div><Footer/></div>
    </div>
  );
}

